/* Localizar datos de la oportunidad */
var myqueryQ = "SELECT OpportunityId FROM Quote WHERE Id = '{!Quote.Id}' limit 1";
result = sforce.connection.query(myqueryQ);
records = result.getArray("records");
if(records[0])
{
    var regQuote = records[0];
    var OppId = regQuote.OpportunityId;
 /*   alert(regQuote.OpportunityId);  */

    var myqueryO = "SELECT Name, OwnerId FROM Opportunity WHERE Id = '" + regQuote.OpportunityId + "' limit 1";
    result = sforce.connection.query(myqueryO);
    records = result.getArray("records");
    if(records[0])
    {
        var regOpp = records[0];
        var OppName = regOpp.Name;
        /* BÃºsqueda de nombre de propietario */
        var myqueryU = "SELECT Name FROM User WHERE Id = '" + regOpp.OwnerId + "' limit 1";
        result = sforce.connection.query(myqueryU);
        records = result.getArray("records");
        if(records[0])
        {
            var regUser = records[0];
            var UserName = regUser.Name;
        }


    }
}

/* Enviar email */
var singleRequest = new sforce.SingleEmailMessage();
/*singleRequest.replyTo = "tecnico@ndtrace.com";*/
singleRequest.subject = "Contracte a introduir a Navision";
var QuoteName = "{!Quote.Name}";
singleRequest.plainTextBody =  "Donar d'alta un contracte a Navision \r\n - Oportunitat: " + OppName + "\r\n - Oferta: " +  QuoteName + "\r\n - Resp: " + UserName; 
var myemal = "joan.company@primagas.es";
singleRequest.toAddresses = [myemal];
var sendMailRes = sforce.connection.sendEmail([singleRequest]);
/* alert(singleRequest);  */


/* Activar campo de Enviado_a_Navision__c */
var checktoUpdate = new sforce.SObject("Quote");
checktoUpdate.Id = "{!Quote.Id}";
checktoUpdate.Enviado_a_Navision__c="true";
var result = sforce.connection.update([checktoUpdate]);
